// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";

contract FBTC is ERC20 {
    constructor() ERC20("Flare BTC", "fBTC") {}

    function mint(address to, uint256 amount) external {
        _mint(to, amount);
    }
}

contracts/FTSO.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

contract FTSO {
    uint256 public price;
    address public owner;

    constructor(uint256 _initialPrice) {
        price = _initialPrice;
        owner = msg.sender;
    }

    function setPrice(uint256 _price) external {
        require(msg.sender == owner, "Only owner");
        price = _price;
    }

    function getPrice() external view returns (uint256) {
        return price;
    }
}

contracts/MicroLender.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/MessageHashUtils.sol";
import "./FTSO.sol";

contract MicroLender {
    using ECDSA for bytes32;
    using MessageHashUtils for bytes32;

    struct Loan {
        uint256 collateralAmount;
        uint256 debtAmount;
        uint256 maxLTV; // e.g., 80 for 80%
    }

    IERC20 public collateralToken;
    FTSO public oracle;
    address public admin;

    mapping(address => Loan) public loans;

    event CollateralDeposited(address indexed user, uint256 amount);
    event Borrowed(address indexed user, uint256 amount);
    event Repaid(address indexed user, uint256 amount);

    constructor(address _collateralToken, address _oracle, address _admin) {
        collateralToken = IERC20(_collateralToken);
        oracle = FTSO(_oracle);
        admin = _admin;
    }

    function verifyCredit(uint256 score, bytes memory signature) public {
        // Construct the message hash
        bytes32 messageHash = keccak256(abi.encodePacked(msg.sender, score));
        bytes32 ethSignedMessageHash = messageHash.toEthSignedMessageHash();

        // Recover the signer
        address signer = ethSignedMessageHash.recover(signature);

        require(signer == admin, "Invalid signature");
        require(score >= 700, "Credit score too low");

        loans[msg.sender].maxLTV = 80; // Set 80% LTV for verified users
    }

    function depositCollateral(uint256 amount) external {
        require(collateralToken.transferFrom(msg.sender, address(this), amount), "Transfer failed");
        loans[msg.sender].collateralAmount += amount;
        emit CollateralDeposited(msg.sender, amount);
    }

    function borrow(uint256 amount) external {
        Loan storage loan = loans[msg.sender];
        require(loan.maxLTV > 0, "Credit not verified");

        uint256 price = oracle.getPrice(); // Price of 1 unit of collateral in USD (e.g. 50000)
        // Assuming collateral has 18 decimals and price is in USD
        // Value of collateral = (collateralAmount * price) / 1e18? 
        // Let's assume price is integer USD for 1 full token.
        // Value = collateralAmount * price / 1e18.

        // Let's simplify: MockFBTC has 18 decimals.
        // Price is e.g. 50000 (USD).
        // Collateral Value = (loan.collateralAmount * price) / 1e18.

        uint256 collateralValue = (loan.collateralAmount * price) / 1e18;
        uint256 maxBorrow = (collateralValue * loan.maxLTV) / 100;

        require(loan.debtAmount + amount <= maxBorrow, "Insufficient collateral");

        loan.debtAmount += amount;

        // Send C2FLR (native currency) to user.
        // Contract needs to be funded with C2FLR.
        payable(msg.sender).transfer(amount);

        emit Borrowed(msg.sender, amount);
    }

    function repay() external payable {
        Loan storage loan = loans[msg.sender];
        require(loan.debtAmount > 0, "No active loan");

        uint256 repaymentAmount = msg.value;
        if (msg.value > loan.debtAmount) {
            repaymentAmount = loan.debtAmount;
            uint256 refund = msg.value - loan.debtAmount;
            payable(msg.sender).transfer(refund);
        }

        loan.debtAmount -= repaymentAmount;
        emit Repaid(msg.sender, repaymentAmount);
    }

    // Allow contract to receive C2FLR
    receive() external payable {}
}

